namespace Geissweb\Euvat\Block\System\Config\Field;

use Magento\Framework\View\Element\Context;
use Magento\Framework\View\Element\Html\Select;
use Magento\Tax\Model\System\Config\Source\Tax\Display\Type;

class PriceDisplayType extends Select
{
    /**
     * @var Type
     */
    public $priceDisplayTypes;

    /**
     * PriceDisplayType constructor.
     *
     * @param Context                  $context
     * @param Type                     $priceDisplayTypes
     * @param array                    $data
     */
    public function __construct(
        Context $context,
        Type $priceDisplayTypes,
        array $data = []
    ) {
        parent::__construct($context, $data);
        $this->priceDisplayTypes = $priceDisplayTypes;
    }

    /**
     * Sets name
     *
     * @param string $value
     *
     * @return $this
     */
    public function setInputName(string $value): PriceDisplayType
    {
        $this->setData('name', $value);
        return $this;
    }

    /**
     * Render block HTML
     *
     * @return string
     */
    public function _toHtml(): string
    {
        if (!$this->getOptions()) {
            $options = $this->priceDisplayTypes->toOptionArray();
            foreach ($options as $option) {
                $this->addOption($option['value'], $option['label']);
            }
        }
        return parent::_toHtml();
    }
}
