namespace Geissweb\Euvat\Helper\Compat;

use Magento\Framework\ObjectManagerInterface;
use Magento\Framework\Serialize\Serializer\Json;
use Magento\Framework\Unserialize\Unserialize;

/**
 * Class UnserializerFactory gives the best unserializer.
 */
class UnserializerFactory
{

    /**
     * @var \Magento\Framework\ObjectManagerInterface
     */
    protected $objectManager;

    /**
     * JsonFactory constructor.
     *
     * @param \Magento\Framework\ObjectManagerInterface $objectManager
     */
    public function __construct(
        ObjectManagerInterface $objectManager
    ) {
        $this->objectManager = $objectManager;
    }

    /**
     * \Magento\Framework\Serialize\Serializer\Json introduced in 2.2.0
     * \Magento\Framework\Unserialize\Unserialize is deprecated
     *
     * @param array $data
     *
     * @return mixed
     */
    public function create(array $data = [])
    {
        if (class_exists(Json::class)) {
            $instanceName =  Json::class;
        } else {
            $instanceName = Unserialize::class;
        }
        return $this->objectManager->create($instanceName, $data);
    }
}
