namespace Geissweb\Euvat\Helper\VatNumber;

class Formatter
{
    /**
     * Format VAT Number
     *
     * @param string|null $vatNumber
     * @param string|null $countryCode
     *
     * @return bool|string
     */
    public function formatVatNumber(?string $vatNumber, ?string $countryCode)
    {
        if ($vatNumber != null) {
            $vatNumber = str_replace(['GST', 'VAT', 'USt-ID', ' ', "\t", "\n"], '', $vatNumber);
            if ($countryCode != null && preg_match('/^[A-Z][A-Z]/i', $vatNumber) === 0) {
                if ($countryCode === 'GR') {
                    $countryCode = 'EL';
                }
                $vatId = $countryCode . $vatNumber;
            } else {
                $vatId = $vatNumber;
            }
            return strtoupper(preg_replace("/[^a-zA-Z0-9]+/", "", $vatId));
        }
        return false;
    }

    /**
     * Extracts Country from VAT-ID
     *
     * Greece and Northern Ireland have different VAT number prefix
     *
     * @param string $vatId
     *
     * @return string
     */
    public function extractCountryIdFromVatId(string $vatId): string
    {
        if (preg_match('/^[A-Z][A-Z]/', $vatId) == 1) {
            $country = strtoupper(substr($vatId, 0, 2));
            if ($country === 'EL') {
                $country = 'GR';
            }
            if ($country === 'XI') {
                $country = 'GB';
            }
            return $country;
        }
        return '';
    }

    /**
     * Splits VAT number into array with country code and the number
     *
     * @param string $vatId
     * @param bool $withoutCountryTransformation
     * @return array
     */
    public function splitVatNumber(string $vatId, bool $withoutCountryTransformation = false): array
    {
        return [
            'cc' => $withoutCountryTransformation ?
                strtoupper(substr($vatId, 0, 2)) : $this->extractCountryIdFromVatId($vatId),
            'number' => substr($vatId, 2)
        ];
    }
}
