namespace Geissweb\Euvat\Model\System\Config\Source;

use Magento\Customer\Model\Customer\Source\Group;
use Magento\Framework\Data\OptionSourceInterface;

/**
 * Class EuCountries
 */
class CustomerGroups extends Group implements OptionSourceInterface
{
    /**
     * Options getter
     *
     * @return array
     */
    public function toOptionArray(): array
    {
        $groups = parent::toOptionArray();
        array_unshift($groups, [
            'label' => __('-- NONE --'),
            'value' => '',
        ]);
        return $groups;
    }
}
