namespace Geissweb\Euvat\Setup;

use Magento\Framework\Setup\ModuleContextInterface;
use Magento\Framework\Setup\SchemaSetupInterface;
use Magento\Framework\Setup\UninstallInterface;

/**
 * For uninstallation
 * Class Uninstall
 */
class Uninstall implements UninstallInterface
{

    /**
     * Uninstall Script
     *
     * @param SchemaSetupInterface   $setup
     * @param ModuleContextInterface $context
     * @return void
     */
    public function uninstall(SchemaSetupInterface $setup, ModuleContextInterface $context)
    {
        $setup->startSetup();

        // remove vat_validation table
        $setup->getConnection()->dropTable($setup->getTable('vat_validation'));

        // remove additional columns on quote, order and address entity tables
        $connection = $setup->getConnection();
        $quoteAddressTable = $connection->getTableName($setup->getTable('quote_address'));
        $orderAddressTable = $connection->getTableName($setup->getTable('sales_order_address'));
        $customerAddressTable = $setup->getTable('customer_address_entity');

        $connection->dropColumn($quoteAddressTable, 'vat_trader_name');
        $connection->dropColumn($quoteAddressTable, 'vat_trader_address');

        $connection->dropColumn($orderAddressTable, 'vat_trader_name');
        $connection->dropColumn($orderAddressTable, 'vat_trader_address');

        $connection->dropColumn($customerAddressTable, 'vat_trader_name');
        $connection->dropColumn($customerAddressTable, 'vat_trader_address');

        $setup->endSetup();
    }
}
