define([
    'mage/utils/wrapper'
],function (
    Wrapper
) {
    "use strict";

    return function (originalRules) {
        originalRules.getObservableFields = Wrapper.wrap(
            originalRules.getObservableFields,
            function (originalAction) {
                var fields = originalAction();
                fields.push('vatId');
                return fields;
            }
        );
        return originalRules;
    };
});